settings.css:
.site-header{
    background: #f2f3ec;
padding:10px 0;
}
.site-nav__link{
color:#000; 
text-transform: uppercase;
padding-left:20px;
padding-right:20px;
font-family: 'CocogooseProTrial Semilight';
    font-weight: normal;
letter-spacing: normal;
}
.site-header__logo{
margin:0;
}
.site-nav__link:hover{
color:#000; 
}
.site-nav__link--underline:after{
border-color:#000;
}
.drawer, .mobile-nav&gt;.mobile-nav__item{
background-color:#f2f3ec;
}
.mobile-nav__link{
font-family: 'CocogooseProTrial Semilight';
    text-transform: uppercase;
    font-size: 1em;
}
.drawer__inner .mobile-nav__link, .drawer__close-button, .mobile-nav__social a{
color:#000!important;
}
.mobile-nav__item:after, .drawer__header, .mobile-nav__social-item{
border-color:#000;
}
.header-item .icon-hamburger path{
stroke-width: 1.5px;
}
.site-nav__link--icon
.icon.icon-hamburger{
width: 22px;
    height: 22px;
}
.site-nav__link--icon
.icon.icon-bag{
width: 25px;
    height: 25px;
}
.drawer_logo{
display:none;
}
@media screen and (max-width: 989px){
.site-nav__link{
padding-left:10px;
padding-right:10px;
}
}
.header__icon--cart .icon path{
stroke-width: 1px;
    stroke: #000!important;
}

.site-footer{
border:none; 
}

.site-footer__linklist a{
font-family: 'texgyreadventorregular';
    color: #9b9b9b;
    text-transform: uppercase;
    letter-spacing: 2px;

}
.site-footer #Footer-newsletter_QgbqEm .footer__collapsible{
font-size:16px; 
}
.footer__title {
font-family: 'AmsiProCond';
    font-size: 28px;
    font-weight: normal;
letter-spacing: 1px;
}
.footer__title .heading_inner{
font-family: 'AmsiProCond-Black';
}
@media screen and (max-width: 768px){
.site-footer__linklist li{
        font-size: 16px;
    }
    .footer__title{
        font-size: 22px;
    }
}
